<?php	if (is_singular("blog") || is_post_type_archive("blog")) {		$postlist = get_posts( array(			'posts_per_page' => 5,			'offset' => 0,			'orderby'          => 'date',			'order'            => 'DESC',			'post_type' => 'blog'		));	} else if (is_singular("info") || is_post_type_archive("info")) {		$postlist = get_posts( array(			'posts_per_page' => 5,			'offset' => 0,			'orderby'          => 'date',			'order'            => 'DESC',			'post_type' => 'info'		));	} else {		$postlist = get_posts( array(			'posts_per_page' => 5,			'offset' => 0,			'orderby'          => 'date',			'order'            => 'DESC',			'post_type' => 'post'		));	}	if($postlist) {		echo '<section class="BlogSideColumn BlogSideColumn--recent">';		echo '<h2 class="BlogSideTitle">Recent Post</h2>';		echo '<ul class="BlogSideList">';		foreach( $postlist as $post ) {			$title = $post->post_title;			$link = get_permalink($post->ID);			if(mb_strlen($post->post_title)>50) {				$title= mb_substr($post->post_title,0,50);				echo '<li><a href="' . $link . '">' . $title .'â€¦</a></li>';			} else {				echo '<li><a href="' . $link . '">' . $title . '</a></li>';			}		}		echo '</ul>';		echo '</section>';	}	wp_reset_query();	// echo '<section class="BlogSideColumn">';	// echo '<h2 class="BlogSideTitle">Category</h2>';	// echo '<ul class="BlogSideList">';	// wp_list_categories('title_li=');	// echo '</ul>';	// echo '</section>';		echo '<section class="BlogSideColumn BlogSideColumn--archives">';		echo '<h2 class="BlogSideTitle">Archives</h2>';		echo '<ul class="BlogSideList">';	if (is_singular("blog") || is_post_type_archive("blog")) {		$blog_arc =  wp_get_archives(array(			'type' => 'yearly',			'post_type' => 'blog'		));	} else if (is_singular("info") || is_post_type_archive("info")) {		$info_arc =  wp_get_archives(array(			'type' => 'yearly',			'post_type' => 'info'		));	} else {		wp_get_archives('type=yearly&title_li=');	}		echo '</ul>';		echo '</section>';?>