<?php$cat = get_the_category();$cat_name = $cat[0]->cat_name;$newmark = get_field("newmark");//print_r($cat);//echo $newmark;echo '<div class="BlogBodyHeader newsbody_php">';echo '<div class="BlogBodyInfo">';if ($newmark != "") {echo '<p class="BlogBodyNew">NEW</p>';}echo '<p class="BlogBodyDate">' . get_the_time('Y.m.d') . '</p>';echo '</div>';the_title('<h1 class="BlogBodyTitle">', '</h1>');echo '</div>';if ( have_posts() ) {	echo '<div class="BlogBodyEntry">';	while( have_posts() ) : the_post();		echo the_content();	endwhile;	echo '</div>';}?>