<?php	$postlist = get_posts( array(		'posts_per_page' => 5,		'offset' => 0,		'orderby'          => 'date',		'order'            => 'DESC',		'post_type' => 'news'	));	if($postlist) {		echo '<section class="BlogSideColumn BlogSideColumn--recent">';		echo '<h2 class="BlogSideTitle">Recent Post</h2>';		echo '<ul class="BlogSideList">';		foreach( $postlist as $post ) {			$title = $post->post_title;			$link = get_permalink($post->ID);			if(mb_strlen($post->post_title)>50) {				$title= mb_substr($post->post_title,0,50);				echo '<li><a href="' . $link . '">' . $title . 'â€¦</a></li>';			} else {				echo '<li><a href="' . $link . '">' . $title . '</a></li>';			}		}		echo '</ul>';		echo '</section>';	}	wp_reset_query();	echo '<section class="BlogSideColumn">';	echo '<h2 class="BlogSideTitle">Archives</h2>';	echo '<ul class="BlogSideList">';	$args = array(		'post_type' => 'news',		'type' => 'yearly',		'after' => '',		'title_li' => ''	);	wp_get_archives($args);	echo '</ul>';	echo '</section>';?>